'use strict';

// MODULES //

var tape = require( 'tape' );
var getKeys = require( 'object-keys' ).shim();
var isRegExp = require( '@stdlib/utils/is-regexp' );
var regexp = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof regexp, 'object', 'main export is an object' );
	t.end();
});

tape( 'the exported object contains regular expressions', function test( t ) {
	var keys;
	var i;

	keys = getKeys( regexp );
	for ( i = 0; i < keys.length; i++ ) {
		t.equal( isRegExp( regexp[ keys[i] ] ), true, keys[i]+' is a regular expression' );
	}
	t.end();
});
