'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var logger = require( 'debug' );
var writeFile = require( '@stdlib/fs/write-file' ).sync;
var objectKeys = require( '@stdlib/utils/keys' );
var replace = require( '@stdlib/string/replace' );
var pluck = require( '@stdlib/utils/pluck' );
var contains = require( '@stdlib/assert/contains' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var aliases = require( '@stdlib/namespace/aliases' );
var help = require( '@stdlib/repl/help' );
var parse = require( '@stdlib/_tools/repl-txt/parse' );


// VARIABLES //

var debug = logger( 'repl:info:build' );

// Output file paths:
var OUTPUT_JSON = resolve( __dirname, '..', 'data', 'data.json' );
var OUTPUT_CSV = resolve( __dirname, '..', 'data', 'data.csv' );

var RE_SHORT_DESC_END = /\.\n\n/;
var RE_SIGNATURE = /([A-Za-z0-9._$]+)\((.*)\)$/;


// FUNCTIONS //

/**
* Wraps a signature.
*
* @private
* @param {string} str - signature string
* @returns {string} wrapped string
*/
function wrap( str ) {
	var len;
	var tmp;
	var s1;
	var s2;
	var i;
	var j;

	len = str.length;
	tmp = str;

	// Check that the signature does not extend beyond 80 characters...
	i = 0; // character index offset
	while ( len > 80 ) {
		// Break the line at the last comma separating parameters before 80 characters...
		for ( j = 79; j >= 0; j-- ) {
			s2 = tmp.slice( ( j > 2 ) ? j-2 : 0, j+1 );
			s1 = s2.slice( 1 );
			if (
				// Note: the following are based on observed patterns. No guarantees of robustness!
				s1 === ', ' ||
				s1 === ',]' ||
				s2 === ', ]' ||
				s2 === ',] '
			) {
				i += j + 1;
				tmp = '  ' + str.slice( i );
				str = str.slice( 0, i ) + '\n' + tmp;
				i += 1; // update offset to include added newline character
				break;
			}
		}
		// Check that a single parameter was not the cause of the overflow (if so, not much we can do unless we were to split on the union type separator `|`, which may be desirable, but, unless this becomes an issue, we don't bother going to the effort)...
		if ( j === -1 ) {
			break;
		}
		// Process the next line:
		len = tmp.length;
	}
	return str;
}

/**
* Returns a section AST nodes having a specified title.
*
* @private
* @param {ObjectArray} nodes - section AST nodes
* @param {string} name - section name
* @returns {(Object|null)} AST node or null
*/
function findSection( nodes, name ) {
	var i;
	for ( i = 0; i < nodes.length; i++ ) {
		if ( nodes[ i ].title === name ) {
			return nodes[ i ];
		}
	}
	return null;
}

/**
* Filters section tag objects (nodes) based on a list of provided tag names.
*
* @private
* @param {ObjectArray} tags - list of tag objects
* @param {Array} names - list of tag names
* @returns {Array} filtered array
*/
function filterTags( tags, names ) {
	var out;
	var i;

	out = [];
	for ( i = 0; i < tags.length; i++ ) {
		if ( contains( names, tags[ i ].name ) ) {
			out.push( tags[ i ] );
		}
	}
	return out;
}

/**
* Converts an untyped signature to a typed signature.
*
* @private
* @param {string} sig - signature string
* @param {ObjectArray} tags - parameter tags
* @returns {string} typed signature string
*/
function signature2typed( sig, tags ) {
	var params;
	var match;
	var type;
	var tag;
	var N;
	var i;
	var j;

	match = sig.match( RE_SIGNATURE );
	if ( match === null ) {
		debug( 'Unable to extract parameters from signature. Signature: %s', sig );
		return sig;
	}
	params = match[ 2 ].split( ',' );
	if ( params.length !== tags.length ) {
		debug( 'Number of parameters in signature does not equal number of provided tags. Unable to include type information. Tags: %d. Parameters: %d.', tags.length, params.length );
		return sig;
	}
	for ( i = 0; i < tags.length; i++ ) {
		tag = tags[ i ];
		if ( tag.type.type === 'UnionType' ) {
			N = tag.type.elements.length;
			type = '';
			for ( j = 0; j < N; j++ ) {
				type += tag.type.elements[ j ].name;
				if ( j < N-1 ) {
					type += '|';
				}
			}
		} else if ( tag.type.type === 'TypedObject' ) {
			type = tag.type.name + '<' + tag.type.of + '>';
		} else {
			type = tag.type.name;
		}
		params[ i ] = replace( params[ i ], tag.name, tag.name+':'+type );
	}
	sig = match[ 1 ] + '(' + params.join( ',' ) + ')';
	debug( 'Typed signature: %s', sig );

	return sig;
}

/**
* Generates abbreviated help text from a REPL text AST interface node.
*
* @private
* @param {Node} node - AST node
* @returns {string} abbreviated help text
*/
function node2info( node ) {
	var tags;
	var desc;
	var out;
	var tmp;
	var sig;
	var s;

	out = '\n';

	sig = node.signature.raw;
	if ( sig.length === node.signature.name.length ) {
		debug( 'Interface is not documented as a function.' );
	} else {
		s = findSection( node.sections, 'Parameters' );
		if ( s ) {
			// Note: number of tags may not match the number of signature parameters, as the parameters section may also document option properties, etc.
			tags = filterTags( s.tags, pluck( node.signature.parameters, 'name' ) );

			debug( 'Resolving interface signature.' );
			sig = signature2typed( sig, tags );
		}
		sig = wrap( sig );
	}
	out += sig + '\n';

	// Note: descriptions should already be text wrapped at 80 characters, so text wrapping should not be a concern when appending the description text...
	debug( 'Resolving short description.' );
	if ( node.description ) {
		tmp = node.description.raw.split( RE_SHORT_DESC_END );
		if ( tmp.length === 1 ) {
			// Description does not include an extended description:
			desc = tmp[ 0 ];
		} else if ( tmp.length > 1 ) {
			// Description includes an extended description:
			desc = tmp[ 0 ] + '.\n';
		} else {
			debug( 'Unable to resolve short description.' );
		}
	} else {
		debug( 'Unable to resolve short description. Missing interface description.' );
		desc = '';
	}
	out += desc;

	return out;
}

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var alias;
	var fopts;
	var json;
	var keys;
	var tmp;
	var csv;
	var txt;
	var ast;
	var ns;
	var t;
	var i;
	var j;

	debug( 'Generating abbreviated REPL help documentation.' );
	ns = aliases();

	json = {};
	for ( i = 0; i < ns.length; i++ ) {
		alias = ns[ i ];
		debug( 'Resolving the REPL help documentation for alias: %s', alias );
		txt = help( alias );
		if ( txt === null ) {
			debug( 'Unable to resolve REPL help documentation. Skipping...', alias );
			continue;
		}
		debug( 'Successfully resolved REPL help documentation. Generating AST...' );
		ast = parse( txt );

		debug( 'Number of documented interfaces: %d', ast.length );
		debug( 'Generating abbreviated help for each documented interface...' );
		for ( j = 0; j < ast.length; j++ ) {
			t = ast[ j ].signature.name;
			debug( 'Interface: %s', t );
			if ( t.slice( 0, alias.length ) !== alias ) {
				debug( 'Interface is documented neither as the alias nor an associated method. Skipping...' );
				continue;
			}
			if ( hasOwnProp( json, t ) ) {
				json[ t ] += node2info( ast[ j ] );
			} else {
				json[ t ] = node2info( ast[ j ] );
			}
			debug( 'Finished processing interface.' );
		}
	}
	debug( 'Writing to JSON file.' );
	fopts = {
		'encoding': 'utf8'
	};
	writeFile( OUTPUT_JSON, JSON.stringify( json ), fopts );

	debug( 'Writing to CSV file.' );
	keys = objectKeys( json );
	csv = '';
	for ( i = 0; i < keys.length; i++ ) {
		tmp = replace( json[ keys[ i ] ], /\r?\n/g, '\\n' );
		tmp = replace( tmp, '"', '\\"' );
		csv += keys[ i ] + ',"' + tmp + '"\n'; // Note: ensures trailing newline
	}
	writeFile( OUTPUT_CSV, csv, fopts );
}


// MAIN //

main();
