'use strict';

// MODULES //

var constants = require( './math/constants.js' );
var baseMathSpecial = require( './math/base.special.js' );
var baseMathTools = require( './math/base.tools.js' );
var baseMathUtils = require( './math/base.utils.js' );
var genericMathUtils = require( './math/generics.utils.js' );
var regexp = require( './regexp' );
var tools = require( './tools' );
var utils = require( './utils' );


// CONTEXT //

/**
* Creates a REPL context.
*
* @private
* @returns {Object} REPL context
*
* @example
* var c = context();
* // returns {...}
*/
function create() {
	var context = {};

	constants( context );

	baseMathSpecial( context );
	baseMathTools( context );
	baseMathUtils( context );

	genericMathUtils( context );

	regexp( context );
	tools( context );
	utils( context );

	return context;
} // end FUNCTION create()


// EXPORTS //

module.exports = create;
