'use strict';

// MODULES //

var constants = require( './math/constants.js' );
var mBaseSpecial = require( './math/base.special.js' );
var mBaseTools = require( './math/base.tools.js' );
var mBaseUtils = require( './math/base.utils.js' );
var mGenericsUtils = require( './math/generics.utils.js' );
var utils = require( './utils' );


// CONTEXT //

/**
* Creates a REPL context.
*
* @private
* @returns {Object} REPL context
*
* @example
* var c = context();
* // returns {...}
*/
function create() {
	var context = {};

	constants( context );

	mBaseSpecial( context );
	mBaseTools( context );
	mBaseUtils( context );

	mGenericsUtils( context );

	utils( context );

	return context;
} // end FUNCTION create()


// EXPORTS //

module.exports = create;
