'use strict';

var NS = {};


// MODULES //

var getKeys = require( 'object-keys' ).shim();
NS.evalpoly = require( '@stdlib/math/base/tools/evalpoly' );
NS.evalrational = require( '@stdlib/math/base/tools/evalrational' );


// VARIABLES //

var KEYS = getKeys( NS );


// BIND //

/**
* Binds functions to a REPL namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* bind( ns );
* // ns => {...}
*/
function bind( ns ) {
	var key;
	var i;
	for ( i = 0; i < KEYS.length; i++ ) {
		key = KEYS[ i ];
		ns[ key ] = NS[ key ];
	}
	return ns;
} // end FUNCTION bind()


// EXPORTS //

module.exports = bind;
