'use strict';

// MODULES //

var baseSpecial = require( './base.special.js' );
var baseTools = require( './base.tools.js' );
var baseUtils = require( './base.utils.js' );
var genericUtils = require( './generics.utils.js' );


// BIND //

/**
* Binds functions to a REPL namespace.
*
* @private
* @param {Object} ns - namespace
* @returns {Object} input namespace
*
* @example
* var ns = {};
* bind( ns );
* // ns => {...}
*/
function bind( ns ) {
	baseSpecial( ns );
	baseTools( ns );
	baseUtils( ns );

	genericUtils( ns );

	return ns;
} // end FUNCTION bind()


// EXPORTS //

module.exports = bind;
