'use strict';

// MODULES //

var repl = require( 'repl' );


// OPTIONS //

/**
* Returns default REPL options.
*
* @private
* @returns {Object} default REPL options
*
* @example
* var opts = options();
* // returns {...}
*/
function options() {
	var opts = {
		'prompt': '> ',
		'input': process.stdin,
		'output': process.stdout,
		'useGlobal': false,
		'ignoreUndefined': true,
		'replMode': repl.REPL_MODE_MAGIC
	};
	return opts;
} // end FUNCTION options()


// EXPORTS //

module.exports = options;
