'use strict';

// MODULES //

var DOCS = require( './docs.js' );


// MAIN //

/**
* Returns help information.
*
* @private
* @param {string} alias - variable alias
*/
function help( alias ) {
	var txt = DOCS[ alias ];
	if ( txt === void 0 ) {
		console.log( 'No help information available.' );
	} else {
		console.log( txt );
	}
} // end FUNCTION help()


// EXPORTS //

module.exports = help;
