/* eslint-disable no-underscore-dangle */

'use strict';

// MODULES //

var logger = require( 'debug' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;


// VARIABLES //

var debug = logger( 'repl:presentation:command:load' );


// MAIN //

/**
* Returns a callback to be invoked upon calling the `load` command.
*
* @private
* @param {Presentation} pres - presentation instance
* @returns {Function} callback
*/
function command( pres ) {
	return onCommand;

	/**
	* Loads a presentation from a file.
	*
	* ## Notes
	*
	* -   Relative file paths are resolved relative to the current working directory.
	*
	* @private
	* @param {string} file - file path
	*/
	function onCommand( file ) {
		var err;
		if ( !isString( file ) ) {
			err = new TypeError( 'invalid argument. Must provide a string. Value: `' + file + '`.' );
			debug( 'Error: %s', err.message );
			pres._repl._ostream.write( 'Error: '+err.message+'\n' );
			return;
		}
		pres._repl.once( 'drain', onDrain );

		/**
		* Callback invoked upon a `drain` event.
		*
		* @private
		*/
		function onDrain() {
			try {
				pres.load( file ).show();
			} catch ( error ) {
				debug( 'Error: %s', error.message );
				pres._repl._ostream.write( 'Error: '+error.message+'\n' );
			}
		}
	}
}


// EXPORTS //

module.exports = command;
