tape( 'the function starts a REPL environment', function test( t ) {
	repl( onStart );

	function onStart( error, server ) {
		if ( error ) {
			t.ok( false, error.message );
			return t.end();
		}
		t.ok( true, 'starts a REPL' );
		setTimeout( onTimeout, 0 );

		function onTimeout() {
			// In Node v0.10, the REPLServer instance does not have a `close()` method...
			if ( server.close ) {
				server.close();
			} else {
				server.rli.close();
			}
			setTimeout( onEnd, 2500 );
		}
	}

	function onEnd() {
		t.end();
	}
});

tape( 'the function starts a REPL environment (options)', function test( t ) {
	repl( {}, onStart );

	function onStart( error, server ) {
		if ( error ) {
			t.ok( false, error.message );
			return t.end();
		}
		t.ok( true, 'starts a REPL' );
		setTimeout( onTimeout, 0 );

		function onTimeout() {
			// In Node v0.10, the REPLServer instance does not have a `close()` method...
			if ( server.close ) {
				server.close();
			} else {
				server.rli.close();
			}
			setTimeout( onEnd, 2500 );
		}
	}

	function onEnd() {
		t.end();
	}
});
