import iterPulseWave = require( './index' );


// TESTS //

// The function returns an iterator...
{
	iterPulseWave(); // $ExpectType Iterator
	iterPulseWave( {} ); // $ExpectType Iterator
	iterPulseWave( { 'iter': 10 } ); // $ExpectType Iterator
}

// The compiler throws an error if the function is provided a first argument which is not an options object...
{
	iterPulseWave( null ); // $ExpectError
}

// The compiler throws an error if the function is provided a `duration` option which is not a number...
{
	iterPulseWave( { 'duration': '5' } ); // $ExpectError
	iterPulseWave( { 'duration': true } ); // $ExpectError
	iterPulseWave( { 'duration': false } ); // $ExpectError
	iterPulseWave( { 'duration': null } ); // $ExpectError
	iterPulseWave( { 'duration': [] } ); // $ExpectError
	iterPulseWave( { 'duration': {} } ); // $ExpectError
	iterPulseWave( { 'duration': ( x: number ): number => x } ); // $ExpectError
}

