'use strict';

// MODULES //

var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' );
var hasOwnProp = require( '@stdlib/assert/has-own-property' );
var roundn = require( '@stdlib/math/base/special/roundn' );
var repeat = require( '@stdlib/string/repeat' );
var max = require( '@stdlib/math/base/special/max' );
var isBoolean = require( '@stdlib/assert/is-boolean' );


// FUNCTIONS //

/**
* Returns n spaces.
*
* @private
* @param {integer} n - number of spaces
* @returns {string} n spaces
*/
function spaces( n ) {
	if ( n <= 0 ) {
		return '';
	}
	return repeat( ' ', n );
}


// MAIN //

/**
* Returns a function to pretty print test results.
*
* @private
* @param {Object} results - test results
* @returns {Function} pretty print function
*/
function prettyPrint( results ) {
	return print;

	/**
	* Pretty-print output of ANOVA.
	*
	* @private
	* @param {Object} [opts] - options object
	* @param {PositiveInteger} [opts.numdigits=4] - number of digits after the decimal point
	* @param {boolean} [opts.decision=true] - Whether or not to print the decision
	* @returns {string} formatted results
	*/
	function print( opts ) {
		var decision;
		var treatMSS;
		var treatSS;
		var errMSS;
		var extra1;
		var extra2;
		var errSS;
		var ndgts;
		var dgts;
		var str;

		dgts = 4;
		decision = true;
		if ( arguments.length > 0 ) {
			if ( hasOwnProp( opts, 'numdigits' ) ) {
				if ( !isPositiveInteger( opts.numdigits ) ) {
					throw new TypeError( 'invalid option. numdigits option must be a positive integer. Option: `' + opts.numdigits + '`.' );
				}
				dgts = opts.numdigits;
			}
			if ( hasOwnProp( opts, 'decision' ) ) {
				if ( !isBoolean( opts.decision ) ) {
					throw new TypeError( 'Decision option must be boolean: `' + opts.decision + '`.' );
				}
				decision = opts.decision;
			}
		}
		ndgts = -dgts;

		str = '';
		str += results.method;
		str += '\n\n';

		// Hypothesis
		str += 'Null Hypothesis: All Means Equal';
		str += '\n';
		str += 'Alternate Hypothesis: At Least one Mean not Equal';
		str += '\n\n';

		treatSS = roundn( results.treatment.ss, ndgts ).toString();
		errSS = roundn( results.error.ss, ndgts ).toString();
		treatMSS = roundn( results.treatment.mss, ndgts ).toString();
		errMSS = roundn( results.error.mss, ndgts ).toString();

		extra1 = max( max( treatSS.length, errSS.length ), 2 );
		extra2 = max( max( treatMSS.length, errMSS.length ), 3 );

		// Formatted table
		str += '              ';
		str += 'df';
		str += spaces( 3 );
		str += 'SS';
		str += spaces( 2 + extra1 );
		str += 'MSS';
		str += spaces( extra2 );
		str += 'F Score';
		str += spaces( dgts - 1 );
		str += 'P Value';
		str += '\n';

		// Now start adding in values
		str += 'Treatment';
		str += spaces( 5 );
		str += results.treatment.df;
		str += spaces( 4 );

		str += treatSS;
		str += spaces( 4 + extra1 - treatSS.length );
		str += treatMSS;
		str += spaces( 3 + extra2 - treatMSS.length );
		str += roundn( results.statistic, ndgts );
		str += spaces( dgts );
		str += roundn( results.pValue, ndgts );
		str += '\n';

		// Next line
		str += 'Errors';
		str += '        ';
		str += results.error.df;
		str += spaces( dgts );
		str += errSS;
		str += spaces( 4 + extra1 - errSS.length );
		str += errMSS;

		if ( decision ) {
			str += '\n\n';
			if ( results.rejected ) {
				str += 'Reject Null: ';
				str += roundn( results.pValue, ndgts );
				str += ' <= ';
				str += results.alpha;
			} else {
				str += 'Fail to Reject Null: ';
				str += roundn( results.pValue, ndgts );
				str += ' >= ';
				str += results.alpha;
			}
		}
		return str;
	}
}


// EXPORTS //

module.exports = prettyPrint;
