#include "stdlib/stats/base/dists/binomial/cdf.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/constants/float64/pinf.h"
#include "stdlib/math/base/assert/is_nonnegative_integer.h"
#include "stdlib/math/base/special/factorial.h"
#include "stdlib/math/base/special/pow.h"
#include <stdint.h>

/**
* Returns the cumulative distribution function (CDF) for a binomial distribution with number of trials `n` and success probability `p`.
*
* @param x  input value
* @param n  number of trials
* @param p  success probability
* @return   cdf
*
* @example
* double y = stdlib_base_dists_binomial_cdf( 3.0, 20, 0.2 );
* // returns ~0.411
*/
double stdlib_base_dists_binomial_cdf( const int32_t x, const int32_t n, const double p ) {
	if (
		stdlib_base_is_nan( p ) ||
		n < 0 ||
		p < 0.0 ||
		p > 1.0
	) {
		return 0.0 / 0.0;
	}
	if ( x < 0 ) {
		return 0.0;
	}
	if ( x >= n ) {
		return 1.0;
	}
	double cdf = 0.0;
	for ( double k = 0.0; k <= x; k++ ) {
		double comb = stdlib_base_factorial( n ) / (stdlib_base_factorial( k ) * stdlib_base_factorial( n - k ));
		cdf += comb * stdlib_base_pow( p, k ) * stdlib_base_pow( 1.0 - p, n - k );
	}

	return cdf;
}
