#include "stdlib/stats/base/dists/binomial/quantile.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/constants/float64/pinf.h"
#include "stdlib/math/base/assert/is_nonnegative_integer.h"
#include "stdlib/math/base/special/factorial.h"
#include "stdlib/math/base/special/pow.h"

/**
* Returns the quantile function of a binomial distribution.
*
* @param r  input probability
* @param n  number of trials
* @param p  success probability
* @return   quantile
*
* @example
* double y = stdlib_base_dists_binomial_quantile( 0.4, 20, 0.2 );
* // returns 3
*/
double stdlib_base_dists_binomial_quantile( const double r, const double n, const double p ) {
	if (
		stdlib_base_is_nan( r ) ||
		stdlib_base_is_nan( n ) ||
		stdlib_base_is_nan( p ) ||
		n < 0.0 ||
		p < 0.0 ||
		p > 1.0 ||
		r < 0.0 ||
		r > 1.0 ||
		!stdlib_base_is_nonnegative_integer( n ) ||
		n == STDLIB_CONSTANT_FLOAT64_PINF
	) {
		return 0.0 / 0.0;
	}

	if ( r == 0.0 ) {
		return 0.0;
	}
	if ( r == 1.0 ) {
		return n;
	}

	double cumulative = 0.0;
	for ( double k = 0.0; k <= n; k++ ) {
		double comb = stdlib_base_factorial( n ) / (stdlib_base_factorial( k ) * stdlib_base_factorial( n - k ));
		cumulative += comb * stdlib_base_pow( p, k ) * stdlib_base_pow( 1.0 - p, n - k );
		if ( cumulative >= r ) {
			return k;
		}
	}

	return n; // Should never reach here
}
