// TypeScript Version: 2.0

/* tslint:disable:max-line-length */
/* tslint:disable:max-file-line-count */

import cdf = require( '@stdlib/stats/base/dists/cosine/cdf' );
import Cosine = require( '@stdlib/stats/base/dists/cosine/ctor' );
import kurtosis = require( '@stdlib/stats/base/dists/cosine/kurtosis' );
import logcdf = require( '@stdlib/stats/base/dists/cosine/logcdf' );
import logpdf = require( '@stdlib/stats/base/dists/cosine/logpdf' );
import mean = require( '@stdlib/stats/base/dists/cosine/mean' );
import median = require( '@stdlib/stats/base/dists/cosine/median' );
import mgf = require( '@stdlib/stats/base/dists/cosine/mgf' );
import mode = require( '@stdlib/stats/base/dists/cosine/mode' );
import pdf = require( '@stdlib/stats/base/dists/cosine/pdf' );
import quantile = require( '@stdlib/stats/base/dists/cosine/quantile' );
import skewness = require( '@stdlib/stats/base/dists/cosine/skewness' );
import stdev = require( '@stdlib/stats/base/dists/cosine/stdev' );
import variance = require( '@stdlib/stats/base/dists/cosine/variance' );

/**
* Interface describing the `cosine` namespace.
*/
interface COSINE {
	/**
	* Raised cosine distribution cumulative distribution function (CDF).
	*
	* @param x - input value
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns evaluated CDF
	*
	* @example
	* var y = cdf( 0.5, 0.0, 1.0 );
	* // returns ~0.909
	*
	* var mycdf = cdf.factory( 3.0, 1.5 );
	*
	* y = mycdf( 4.0 );
	* // returns ~0.971
	*/
	cdf: typeof cdf;

	/**
	* Cosine distribution.
	*/
	Cosine: typeof Cosine;

	/**
	* Returns the excess kurtosis for a raised cosine distribution with location `mu` and scale `s`.
	*
	* ## Notes
	*
	* -   If provided `s <= 0`, the function returns `NaN`.
	*
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns excess kurtosis
	*
	* @example
	* var y = kurtosis( 0.0, 1.0 );
	* // returns ~-0.594
	*
	* @example
	* var y = kurtosis( 5.0, 2.0 );
	* // returns ~-0.594
	*
	* @example
	* var y = kurtosis( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = kurtosis( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = kurtosis( 0.0, 0.0 );
	* // returns NaN
	*/
	kurtosis: typeof kurtosis;

	/**
	* Raised cosine distribution logarithm of cumulative distribution function (logCDF).
	*
	* @param x - input value
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns evaluated logCDF
	*
	* @example
	* var y = logcdf( 0.5, 0.0, 1.0 );
	* // returns ~-0.095
	*
	* var mylogcdf = logcdf.factory( 3.0, 1.5 );
	*
	* y = mylogcdf( 4.0 );
	* // returns ~--0.029
	*/
	logcdf: typeof logcdf;

	/**
	* Raised cosine distribution logarithm of probability density function (PDF).
	*
	* @param x - input value
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns evaluated logPDF
	*
	* @example
	* var y = logpdf( 2.0, 0.0, 1.0 );
	* // returns ~-2.254
	*
	* var mylogpdf = logpdf.factory( 10.0, 2.0 );
	* y = mylogpdf( 10.0 );
	* // returns ~-0.693
	*/
	logpdf: typeof logpdf;

	/**
	* Returns the expected value for a raised cosine distribution with location `mu` and scale `s`.
	*
	* ## Notes
	*
	* -   If provided `s <= 0`, the function returns `NaN`.
	*
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns expected value
	*
	* @example
	* var y = mean( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = mean( 5.0, 2.0 );
	* // returns 5.0
	*
	* @example
	* var y = mean( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = mean( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = mean( 0.0, 0.0 );
	* // returns NaN
	*/
	mean: typeof mean;

	/**
	* Returns the median for a raised cosine distribution with location `mu` and scale `s`.
	*
	* ## Notes
	*
	* -   If provided `s <= 0`, the function returns `NaN`.
	*
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns median
	*
	* @example
	* var y = median( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = median( 5.0, 2.0 );
	* // returns 5.0
	*
	* @example
	* var y = median( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = median( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = median( 0.0, 0.0 );
	* // returns NaN
	*/
	median: typeof median;

	/**
	* Raised cosine distribution moment-generating function (MGF).
	*
	* @param t - input value
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns evaluated MGF
	*
	* @example
	* var y = mgf( 0.5, 0.0, 1.0 );
	* // returns ~1.016
	*
	* var myMGF = mgf.factory( 3.0, 1.5 );
	*
	* y = myMGF( 1.0 );
	* // returns ~23.219
	*/
	mgf: typeof mgf;

	/**
	* Returns the mode for a raised cosine distribution with location `mu` and scale `s`.
	*
	* ## Notes
	*
	* -   If provided `s <= 0`, the function returns `NaN`.
	*
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns mode
	*
	* @example
	* var y = mode( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = mode( 5.0, 2.0 );
	* // returns 5.0
	*
	* @example
	* var y = mode( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = mode( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = mode( 0.0, 0.0 );
	* // returns NaN
	*/
	mode: typeof mode;

	/**
	* Raised cosine distribution probability density function (PDF).
	*
	* @param x - input value
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns evaluated PDF
	*
	* @example
	* var y = pdf( 2.0, 0.0, 3.0 );
	* // returns ~0.083
	*
	* var myPDF = pdf.factory( 0.0, 3.0 );
	* y = myPDF( 2.0 );
	* // returns ~0.083
	*/
	pdf: typeof pdf;

	/**
	* Raised cosine distribution quantile function.
	*
	* @param p - input value
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns evaluated quantile function
	*
	* @example
	* var y = quantile( 0.8, 0.0, 1.0 );
	* // returns ~0.327
	*
	* var myQuantile = quantile.factory( 10.0, 2.0 );
	* y = myQuantile( 0.5 );
	* // returns ~10.0
	*/
	quantile: typeof quantile;

	/**
	* Returns the skewness for a raised cosine distribution with location `mu` and scale `s`.
	*
	* ## Notes
	*
	* -   If provided `s <= 0`, the function returns `NaN`.
	*
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns skewness
	*
	* @example
	* var y = skewness( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = skewness( 5.0, 2.0 );
	* // returns 0.0
	*
	* @example
	* var y = skewness( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = skewness( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = skewness( 0.0, 0.0 );
	* // returns NaN
	*/
	skewness: typeof skewness;

	/**
	* Returns the standard deviation for a raised cosine distribution with location `mu` and scale `s`.
	*
	* ## Notes
	*
	* -   If provided `s <= 0`, the function returns `NaN`.
	*
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns standard deviation
	*
	* @example
	* var y = stdev( 0.0, 1.0 );
	* // returns ~0.362
	*
	* @example
	* var y = stdev( 5.0, 2.0 );
	* // returns ~0.723
	*
	* @example
	* var y = stdev( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = stdev( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = stdev( 0.0, 0.0 );
	* // returns NaN
	*/
	stdev: typeof stdev;

	/**
	* Returns the variance for a raised cosine distribution with location `mu` and scale `s`.
	*
	* ## Notes
	*
	* -   If provided `s <= 0`, the function returns `NaN`.
	*
	* @param mu - location parameter
	* @param s - scale parameter
	* @returns variance
	*
	* @example
	* var y = variance( 0.0, 1.0 );
	* // returns ~0.131
	*
	* @example
	* var y = variance( 5.0, 2.0 );
	* // returns ~0.523
	*
	* @example
	* var y = variance( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = variance( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = variance( 0.0, 0.0 );
	* // returns NaN
	*/
	variance: typeof variance;
}

/**
* Raised cosine distribution.
*/
declare var cosine: COSINE;


// EXPORTS //

export = cosine;
