#include "stdlib/stats/base/dists/kumaraswamy/pdf.h"
#include "stdlib/math/base/assert/is_nan.h"
#include <math.h>

/**
* Evaluates the probability density function (PDF) for a Kumaraswamy distribution with shape parameters `alpha` and `beta` at a value `x`.
*
* @param x       input value
* @param alpha   first shape parameter
* @param beta    second shape parameter
* @return        evaluated PDF
*
* @example
* double y = stdlib_base_dists_kumaraswamy_pdf( 0.5, 2.0, 1.0 );
* // returns 1.0
*/
double stdlib_base_dists_kumaraswamy_pdf( const double x, const double alpha, const double beta ) {
	if (
		stdlib_base_is_nan( x ) ||
		stdlib_base_is_nan( alpha ) ||
		stdlib_base_is_nan( beta ) ||
		alpha <= 0.0 ||
		beta <= 0.0
	) {
		return 0.0/0.0; // NaN
	}
	if ( x <= 0.0 || x >= 1.0 ) {
		return 0.0;
	}
	return alpha * beta * pow(x, alpha-1.0) * pow(1.0 - pow(x, alpha), beta-1.0);
}
