// TypeScript Version: 2.0

/* tslint:disable:max-line-length */
/* tslint:disable:max-file-line-count */

import cdf = require( '@stdlib/stats/base/dists/normal/cdf' );
import Normal = require( '@stdlib/stats/base/dists/normal/ctor' );
import entropy = require( '@stdlib/stats/base/dists/normal/entropy' );
import kurtosis = require( '@stdlib/stats/base/dists/normal/kurtosis' );
import logpdf = require( '@stdlib/stats/base/dists/normal/logpdf' );
import mean = require( '@stdlib/stats/base/dists/normal/mean' );
import median = require( '@stdlib/stats/base/dists/normal/median' );
import mgf = require( '@stdlib/stats/base/dists/normal/mgf' );
import mode = require( '@stdlib/stats/base/dists/normal/mode' );
import pdf = require( '@stdlib/stats/base/dists/normal/pdf' );
import quantile = require( '@stdlib/stats/base/dists/normal/quantile' );
import skewness = require( '@stdlib/stats/base/dists/normal/skewness' );
import stdev = require( '@stdlib/stats/base/dists/normal/stdev' );
import variance = require( '@stdlib/stats/base/dists/normal/variance' );

/**
* Interface describing the `normal` namespace.
*/
interface NORMAL {
	/**
	* Normal distribution cumulative distribution function (CDF).
	*
	* @param x - input value
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns evaluated CDF
	*
	* @example
	* var y = cdf( 2.0, 0.0, 1.0 );
	* // returns ~0.977
	*
	* var myCDF = cdf.factory( 10.0, 2.0 );
	* y = myCDF( 10.0 );
	* // returns 0.5
	*/
	cdf: typeof cdf;

	/**
	* Normal distribution.
	*/
	Normal: typeof Normal;

	/**
	* Returns the differential entropy for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns entropy
	*
	* @example
	* var y = entropy( 0.0, 1.0 );
	* // returns ~1.419
	*
	* @example
	* var y = entropy( 5.0, 3.0 );
	* // returns ~2.518
	*
	* @example
	* var y = entropy( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = entropy( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = entropy( 0.0, 0.0 );
	* // returns NaN
	*/
	entropy: typeof entropy;

	/**
	* Returns the excess kurtosis for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns excess kurtosis
	*
	* @example
	* var y = kurtosis( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = kurtosis( 5.0, 3.0 );
	* // returns 0.0
	*
	* @example
	* var y = kurtosis( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = kurtosis( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = kurtosis( 0.0, 0.0 );
	* // returns NaN
	*/
	kurtosis: typeof kurtosis;

	/**
	* Normal distribution natural logarithm of probability density function (logPDF).
	*
	* @param x - input value
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns evaluated logPDF
	*
	* @example
	* var y = logpdf( 2.0, 0.0, 1.0 );
	* // returns ~-2.919
	*
	* var mylogpdf = logpdf.factory( 10.0, 2.0 );
	* y = mylogpdf( 10.0 );
	* // returns ~-1.612
	*/
	logpdf: typeof logpdf;

	/**
	* Returns the expected value for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns expected value
	*
	* @example
	* var y = mean( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = mean( 5.0, 2.0 );
	* // returns 5.0
	*
	* @example
	* var y = mean( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = mean( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = mean( 0.0, 0.0 );
	* // returns NaN
	*/
	mean: typeof mean;

	/**
	* Returns the median for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns median
	*
	* @example
	* var y = median( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = median( 5.0, 2.0 );
	* // returns 5.0
	*
	* @example
	* var y = median( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = median( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = median( 0.0, 0.0 );
	* // returns NaN
	*/
	median: typeof median;

	/**
	* Normal distribution moment-generating function (MGF).
	*
	* @param t - input value
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns evaluated MGF
	*
	* @example
	* var y = mgf( 2.0, 0.0, 1.0 );
	* // returns ~7.389
	*
	* y = mgf( 0.0, 0.0, 1.0 );
	* // returns 1.0
	*
	* y = mgf( -1.0, 4.0, 2.0 );
	* // returns ~0.1353
	*
	* var mymgf = mgf.factory( 4.0, 2.0 );
	*
	* y = mymgf( 1.0 );
	* // returns ~403.429
	*
	* y = mymgf( 0.5 );
	* // returns ~12.182
	*/
	mgf: typeof mgf;

	/**
	* Returns the mode for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns mode
	*
	* @example
	* var y = mode( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = mode( 5.0, 2.0 );
	* // returns 5.0
	*
	* @example
	* var y = mode( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = mode( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = mode( 0.0, 0.0 );
	* // returns NaN
	*/
	mode: typeof mode;

	/**
	* Normal distribution probability density function (PDF).
	*
	* @param x - input value
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns evaluated PDF
	*
	* @example
	* var y = pdf( 2.0, 0.0, 1.0 );
	* // returns ~0.054
	*
	* var myPDF = pdf.factory( 10.0, 2.0 );
	* y = myPDF( 10.0 );
	* // returns ~0.199
	*/
	pdf: typeof pdf;

	/**
	* Normal distribution quantile function.
	*
	* @param p - input value
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns evaluated quantile function
	*
	* @example
	* var y = quantile( 0.8, 0.0, 1.0 );
	* // returns ~0.842
	*
	* var myQuantile = quantile.factory( 10.0, 2.0 );
	* y = myQuantile( 0.5 );
	* // returns 10.0
	*/
	quantile: typeof quantile;

	/**
	* Returns the skewness for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns skewness
	*
	* @example
	* var y = skewness( 0.0, 1.0 );
	* // returns 0.0
	*
	* @example
	* var y = skewness( 5.0, 3.0 );
	* // returns 0.0
	*
	* @example
	* var y = skewness( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = skewness( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = skewness( 0.0, 0.0 );
	* // returns NaN
	*/
	skewness: typeof skewness;

	/**
	* Returns the standard deviation for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns standard deviation
	*
	* @example
	* var y = stdev( 0.0, 1.0 );
	* // returns 1.0
	*
	* @example
	* var y = stdev( 5.0, 3.0 );
	* // returns 3.0
	*
	* @example
	* var y = stdev( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = stdev( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = stdev( 0.0, 0.0 );
	* // returns NaN
	*/
	stdev: typeof stdev;

	/**
	* Returns the variance for a normal distribution with mean `mu` and standard deviation `sigma`.
	*
	* ## Notes
	*
	* -   If provided `sigma <= 0`, the function returns `NaN`.
	*
	* @param mu - mean
	* @param sigma - standard deviation
	* @returns variance
	*
	* @example
	* var y = variance( 0.0, 1.0 );
	* // returns 1.0
	*
	* @example
	* var y = variance( 5.0, 3.0 );
	* // returns 9.0
	*
	* @example
	* var y = variance( NaN, 1.0 );
	* // returns NaN
	*
	* @example
	* var y = variance( 0.0, NaN );
	* // returns NaN
	*
	* @example
	* var y = variance( 0.0, 0.0 );
	* // returns NaN
	*/
	variance: typeof variance;
}

/**
* Normal distribution.
*/
declare var normal: NORMAL;


// EXPORTS //

export = normal;
