// TypeScript Version: 2.0

/* tslint:disable:max-line-length */
/* tslint:disable:max-file-line-count */

import cdf = require( '@stdlib/stats/base/dists/poisson/cdf' );
import Poisson = require( '@stdlib/stats/base/dists/poisson/ctor' );
import kurtosis = require( '@stdlib/stats/base/dists/poisson/kurtosis' );
import logpmf = require( '@stdlib/stats/base/dists/poisson/logpmf' );
import mean = require( '@stdlib/stats/base/dists/poisson/mean' );
import median = require( '@stdlib/stats/base/dists/poisson/median' );
import mgf = require( '@stdlib/stats/base/dists/poisson/mgf' );
import mode = require( '@stdlib/stats/base/dists/poisson/mode' );
import pmf = require( '@stdlib/stats/base/dists/poisson/pmf' );
import quantile = require( '@stdlib/stats/base/dists/poisson/quantile' );
import skewness = require( '@stdlib/stats/base/dists/poisson/skewness' );
import stdev = require( '@stdlib/stats/base/dists/poisson/stdev' );
import variance = require( '@stdlib/stats/base/dists/poisson/variance' );

/**
* Interface describing the `poisson` namespace.
*/
interface POISSON {
	/**
	* Poisson distribution cumulative distribution function (CDF).
	*
	* @param x - input value
	* @param lambda - mean parameter
	* @returns evaluated CDF
	*
	* @example
	* var y = cdf( 2.0, 0.5 );
	* // returns ~0.986
	*
	* y = cdf( 2.0, 10.0 );
	* // returns ~0.003
	*
	* y = cdf( -1.0, 4.0 );
	* // returns 0.0
	*
	* var mycdf = cdf.factory( 5.0 );
	* y = mycdf( 3.0 );
	* // returns ~0.265
	*
	* y = mycdf( 8.0 );
	* // returns ~0.932
	*/
	cdf: typeof cdf;

	/**
	* Poisson distribution.
	*/
	Poisson: typeof Poisson;

	/**
	* Returns the excess kurtosis of a Poisson distribution.
	*
	* ## Notes
	*
	* -   If provided a non-positive value for `λ`, the function returns `NaN`.
	*
	* @param lambda - mean parameter
	* @returns excess kurtosis
	*
	* @example
	* var v = kurtosis( 9.0 );
	* // returns ~0.111
	*
	* @example
	* var v = kurtosis( 1.0 );
	* // returns 1.0
	*
	* @example
	* var v = kurtosis( -0.2 );
	* // returns NaN
	*
	* @example
	* var v = kurtosis( NaN );
	* // returns NaN
	*/
	kurtosis: typeof kurtosis;

	/**
	* Poisson distribution natural logarithm of probability mass function (PMF).
	*
	* @param x - input value
	* @param lambda - mean parameter
	* @returns evaluated logPMF
	*
	* @example
	* var y = logpmf( 4.0, 3.0 );
	* // returns ~-1.784
	*
	* y = logpmf( 1.0, 3.0 );
	* // returns ~-1.901
	*
	* y = logpmf( -1.0, 2.0 );
	* // returns -Infinity
	*
	* var mylogpmf = logpmf.factory( 1.0 );
	* y = mylogpmf( 3.0 );
	* // returns ~-2.797
	*
	* y = mylogpmf( 1.0 );
	* // returns ~-1.0
	*/
	logpmf: typeof logpmf;

	/**
	* Returns the expected value of a Poisson distribution.
	*
	* ## Notes
	*
	* -   If provided a negative value for `λ`, the function returns `NaN`.
	*
	* @param lambda - mean parameter
	* @returns expected value
	*
	* @example
	* var v = mean( 9.0 );
	* // returns 9.0
	*
	* @example
	* var v = mean( 1.0 );
	* // returns 1.0
	*
	* @example
	* var v = mean( -0.2 );
	* // returns NaN
	*
	* @example
	* var v = mean( NaN );
	* // returns NaN
	*/
	mean: typeof mean;

	/**
	* Returns the median of a Poisson distribution.
	*
	* ## Notes
	*
	* -   If provided a negative value for `λ`, the function returns `NaN`.
	*
	* @param lambda - mean parameter
	* @returns median
	*
	* @example
	* var v = median( 9.0 );
	* // returns 9
	*
	* @example
	* var v = median( 1.0 );
	* // returns 1
	*
	* @example
	* var v = median( -0.2 );
	* // returns NaN
	*
	* @example
	* var v = median( NaN );
	* // returns NaN
	*/
	median: typeof median;

	/**
	* Poisson distribution moment-generating function (MGF).
	*
	* @param t - input value
	* @param lambda - mean parameter
	* @returns evaluated MGF
	*
	* @example
	* var v = mgf( 0.5, 0.5 );
	* // returns ~1.383
	*
	* var mymgf = mgf.factory( 2.0 );
	* y = mymgf( 0.1 );
	* // returns ~1.234
	*/
	mgf: typeof mgf;

	/**
	* Returns the mode of a Poisson distribution.
	*
	* ## Notes
	*
	* -   If provided a negative value for `λ`, the function returns `NaN`.
	*
	* @param lambda - mean parameter
	* @returns mode
	*
	* @example
	* var v = mode( 9.0 );
	* // returns 9
	*
	* @example
	* var v = mode( 1.0 );
	* // returns 1
	*
	* @example
	* var v = mode( -0.2 );
	* // returns NaN
	*
	* @example
	* var v = mode( NaN );
	* // returns NaN
	*/
	mode: typeof mode;

	/**
	* Poisson distribution probability mass function (PMF).
	*
	* @param x - input value
	* @param lambda - mean parameter
	* @returns evaluated PMF
	*
	* @example
	* var y = pmf( 4.0, 3.0 );
	* // returns ~0.168
	*
	* y = pmf( 1.0, 3.0 );
	* // returns ~0.149
	*
	* y = pmf( -1.0, 2.0 );
	* // returns 0.0
	*
	* var mypmf = pmf.factory( 1.0 );
	* y = mypmf( 3.0 );
	* // returns ~0.061
	*
	* y = mypmf( 1.0 );
	* // returns ~0.368
	*/
	pmf: typeof pmf;

	/**
	* Poisson distribution quantile function.
	*
	* @param p - input value
	* @param lambda - mean parameter
	* @returns evaluated quantile function
	*
	* @example
	* var y = quantile( 0.5, 2.0 );
	* // returns 2
	*
	* y = quantile( 0.9, 4.0 );
	* // returns 7
	*
	* y = quantile( 0.1, 200.0 );
	* // returns 182
	*
	* var myquantile = quantile.factory( 5.0 );
	* y = myquantile( 0.4 );
	* // returns 4
	*
	* y = myquantile( 0.8 );
	* // returns 7
	*
	* y = myquantile( 1.0 );
	* // returns Infinity
	*/
	quantile: typeof quantile;

	/**
	* Returns the skewness of a Poisson distribution.
	*
	* ## Notes
	*
	* -   If provided a non-positive value for `λ`, the function returns `NaN`.
	*
	* @param lambda - mean parameter
	* @returns skewness
	*
	* @example
	* var v = skewness( 9.0 );
	* // returns ~0.33
	*
	* @example
	* var v = skewness( 1.0 );
	* // returns 1.0
	*
	* @example
	* var v = skewness( -0.2 );
	* // returns NaN
	*
	* @example
	* var v = skewness( NaN );
	* // returns NaN
	*/
	skewness: typeof skewness;

	/**
	* Returns the standard deviation of a Poisson distribution.
	*
	* ## Notes
	*
	* -   If provided a negative value for `λ`, the function returns `NaN`.
	*
	* @param lambda - mean parameter
	* @returns standard deviation
	*
	* @example
	* var v = stdev( 9.0 );
	* // returns 3.0
	*
	* @example
	* var v = stdev( 1.0 );
	* // returns 1.0
	*
	* @example
	* var v = stdev( -0.2 );
	* // returns NaN
	*
	* @example
	* var v = stdev( NaN );
	* // returns NaN
	*/
	stdev: typeof stdev;

	/**
	* Returns the variance of a Poisson distribution.
	*
	* ## Notes
	*
	* -   If provided a negative value for `λ`, the function returns `NaN`.
	*
	* @param lambda - mean parameter
	* @returns variance
	*
	* @example
	* var v = variance( 9.0 );
	* // returns 9.0
	*
	* @example
	* var v = variance( 1.0 );
	* // returns 1.0
	*
	* @example
	* var v = variance( -0.2 );
	* // returns NaN
	*
	* @example
	* var v = variance( NaN );
	* // returns NaN
	*/
	variance: typeof variance;
}

/**
* Poisson distribution.
*/
declare var poisson: POISSON;


// EXPORTS //

export = poisson;
