#include "stdlib/stats/base/dists/triangular/mode.h"
#include <stdlib.h>
#include <stdio.h>

int main( void ) {
    double a, b, c;
    double v;
    int i;

    for ( i = 0; i < 10; i++ ) {
        // Generate a random lower bound 'a':
        a = 20.0 * (double)rand() / ((double)RAND_MAX + 1.0);

        // Generate a random upper bound 'b', ensuring b > a:
        b = 20.0 * (double)rand() / ((double)RAND_MAX + 1.0) + a;

        // Generate a random mode 'c', ensuring a <= c <= b:
        c = a + ( (b - a) * (double)rand() / ((double)RAND_MAX + 1.0) );

        // Compute the mode of the triangular distribution:
        v = stdlib_base_dists_triangular_mode( a, b, c );

        // Print the parameters and the result:
        printf( "a: %lf, b: %lf, c: %lf, Mode(X; a, b, c): %lf\n", a, b, c, v );
    }

    return 0;
}
