'use strict';

// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var getKeys = require( '@stdlib/utils/keys' );


// MAIN //

/**
* Copies test results to a read-only object.
*
* @private
* @param {Object} obj - object to copy
* @returns {Object} read-only object copy
*/
function copyResults( obj ) {
	var keys;
	var out;
	var i;

	keys = getKeys( obj );
	out = {};
	for ( i = 0; i < keys.length; i++ ) {
		setReadOnly( out, keys[ i ], obj[ keys[i] ] );
	}
	return out;
}


// EXPORTS //

module.exports = copyResults;
