'use strict';

// MODULES //

var isPositiveInteger = require( '@stdlib/assert/is-positive-integer' ).isPrimitive;
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var abs = require( '@stdlib/math/base/special/abs' );
var PINF = require( '@stdlib/constants/math/float64-pinf' );


// MAIN //

/**
* Returns an accumulator function which incrementally computes a moving minimum absolute value.
*
* @param {PositiveInteger} W - window size
* @throws {TypeError} must provide a positive integer
* @returns {Function} accumulator function
*
* @example
* var accumulator = incrmminabs( 3 );
*/
function incrmminabs( W ) {
	var buf;
	var min;
	var N;
	var i;
	if ( !isPositiveInteger( W ) ) {
		throw new TypeError( 'invalid input argument. Must provide a positive integer. Value: `' + W + '`.' );
	}
	buf = new Array( W );
	min = PINF;
	i = -1;
	N = 0;

	return accumulator;

	/**
	* If provided a value, the accumulator function returns an updated minimum absolute value. If not provided a value, the accumulator function returns the current minimum absolute value.
	*
	* @private
	* @param {number} [x] - input value
	* @returns {(number|null)} minimum absolute value or null
	*
	* @example
	* var m = accumulator();
	* // returns null
	*
	* m = accumulator( 2.0 );
	* // returns 2.0
	*
	* m = accumulator( -5.0 );
	* // returns 2.0
	*
	* m = accumulator( 3.0 );
	* // returns 2.0
	*
	* m = accumulator( 5.0 );
	* // returns 2.0
	*
	* m = accumulator();
	* // returns 2.0
	*/
	function accumulator( x ) {
		var ax;
		var v;
		var k;
		if ( arguments.length === 0 ) {
			if ( N === 0 ) {
				return null;
			}
			return min;
		}
		// Update the index for managing the circular buffer:
		i = (i+1) % W;

		// Compute the absolute value:
		ax = abs( x );

		// Case: update initial window...
		if ( N < W ) {
			N += 1;
			if ( isnan( x ) || ax < min ) {
				min = ax;
			}
		}
		// Case: incoming value is NaN or less than current minimum value...
		else if ( isnan( x ) || ax < min ) {
			min = ax;
		}
		// Case: outgoing value is the current minimum and the new value is greater than the minimum, and, thus, we need to find a new minimum among the current values...
		else if ( ( buf[ i ] === min && ax > min ) || isnan( buf[ i ] ) ) {
			min = ax;
			for ( k = 0; k < W; k++ ) {
				if ( k !== i ) {
					v = buf[ k ];
					if ( isnan( v ) ) {
						min = v;
						break; // no need to continue searching
					}
					if ( v < min ) {
						min = v;
					}
				}
			}
		}
		// Case: updating existing window; however, the minimum absolute value does not change so nothing to do but update our buffer...
		buf[ i ] = ax;

		return min;
	}
}


// EXPORTS //

module.exports = incrmminabs;
