'use strict';

// MODULES //

var tape = require( 'tape' );
var stdin = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof stdin, 'object', 'main export is an object' );
	t.end();
});

tape( 'the export is an alias for `process.stdin`', function test( t ) {
	t.equal( stdin, process.stdin, 'is an alias' );
	t.end();
});
