'use strict';

// MODULES //

var isObject = require( '@stdlib/utils/is-object' ); // TODO: is-plain-object
var isBoolean = require( '@stdlib/utils/is-boolean' ).isPrimitive;
var isPositiveNumber = require( '@stdlib/utils/is-positive-number' ).isPrimitive;
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// VALIDATE //

/**
* Validates function options.
*
* @private
* @param {Object} opts - destination object
* @param {Options} options - function options
* @param {PositiveNumber} [options.epsilon] - insensitivity parameter
* @param {PositiveNumber} [options.eta0] - constant learning rate
* @param {PositiveNumber} [options.lambda] - regularization parameter
* @param {string} [options.learningRate] - the learning rate to use
* @param {string} [options.loss] -  the loss function to use
* @param {boolean} [options.intercept] - specifies whether an intercept should be included
* @returns {(Error|null)} null or an error object
*
* @example
* var opts = {};
* var options = {};
* var err = validate( opts, options );
* if ( err ) {
*     throw err;
* }
*/
function validate( opts, options ) {
	if ( !isObject( options ) ) {
		return new TypeError( 'invalid input argument. Options must be an object. Value: `' + options + '`.' );
	}
	if ( options.hasOwnProperty( 'epsilon' ) ) {
		opts.epsilon = options.epsilon;
		if ( !isPositiveNumber( opts.epsilon ) ) {
			return new TypeError( 'invalid option. `epsilon` option must be a positive number. Option: `' + opts.highWaterMark + '`.' );
		}
	}
	if ( options.hasOwnProperty( 'eta0' ) ) {
		opts.eta0 = options.eta0;
		if ( !isPositiveNumber( opts.eta0 ) ) {
			return new TypeError( 'invalid option. `eta0` option must be a positive number. Option: `' + opts.highWaterMark + '`.' );
		}
	}
	if ( options.hasOwnProperty( 'lambda' ) ) {
		opts.lambda = options.lambda;
		if ( !isPositiveNumber( opts.lambda ) ) {
			return new TypeError( 'invalid option. `lambda` option must be a positive number. Option: `' + opts.highWaterMark + '`.' );
		}
	}
	if ( options.hasOwnProperty( 'learningRate' ) ) {
		opts.learningRate = options.learningRate;
		if ( !isString( opts.learningRate ) ) {
			return new TypeError( 'invalid option. `learningRate` option must be a primitive string. Option: `' + opts.sep + '`.' );
		}
	}
	if ( options.hasOwnProperty( 'loss' ) ) {
		opts.loss = options.loss;
		if ( !isString( opts.loss ) ) {
			return new TypeError( 'invalid option. `loss` option must be a primitive string. Option: `' + opts.sep + '`.' );
		}
	}
	if ( options.hasOwnProperty( 'intercept' ) ) {
		opts.intercept = options.intercept;
		if ( !isBoolean( opts.intercept ) ) {
			return new TypeError( 'invalid option. `intercept` option must be a primitive boolean. Option: `' + opts.allowHalfOpen + '`.' );
		}
	}
	return null;
} // end FUNCTION validate()


// EXPORTS //

module.exports = validate;
