'use strict';

// MODULES //

var tape = require( 'tape' );
var stderr = require( './../lib' );


// TESTS //

tape( 'main export is an object', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof stderr, 'object', 'main export is an object' );
	t.end();
});

tape( 'the export is an alias for `process.stderr`', function test( t ) {
	t.equal( stderr, process.stderr, 'is an alias' );
	t.end();
});
