'use strict';

/**
* Transform stream for debugging pipelines.
*
* @module @stdlib/streams/utils/debug
*
* @example
* var debugStream = require( '@stdlib/streams/utils/debug' );
*
* var stream = debugStream();
*
* stream.write( 'a' );
* stream.write( 'b' );
* stream.write( 'c' );
* stream.end();
*
* @example
* var debugStream = require( '@stdlib/streams/utils/debug' );
*
* var stream = debugStream.objectMode();
*
* stream.write( {'value': 'a'} );
* stream.write( {'value': 'b'} );
* stream.write( {'value': 'c'} );
* stream.end();
*
* @example
* var debugStream = require( '@stdlib/streams/utils/debug' );
*
* var opts = {
*     'objectMode': true,
*     'highWaterMark': 64
* };
*
* var factory = debugStream.factory( opts );
*
* // Create 10 identically configured streams...
* var streams = [];
* var i;
* for ( i = 0; i < 10; i++ ) {
*     streams.push( factory( 'stream '+i ) );
* }
*/


// MODULES //

var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var stream = require( './stream.js' );
var objectMode = require( './object_mode.js' );
var factory = require( './factory.js' );


// METHODS //

setReadOnly( stream, 'objectMode', objectMode );
setReadOnly( stream, 'factory', factory );


// EXPORTS //

module.exports = stream;
