'use strict';

// MODULES //

var logger = require( 'debug' );


// VARIABLES //

var debug = logger( 'inspect-stream' );


// MAIN //

/**
* Implements the `_flush` method.
*
* @private
* @param {Callback} clbk - callback to invoke after performing flush tasks
*/
function flush( clbk ) {
	debug( 'Flushing the stream...' );
	clbk();
} // end FUNCTION flush()


// EXPORTS //

module.exports = flush;
