'use strict';

// MODULES //

var tape = require( 'tape' );
var splitStream = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof splitStream, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to main export is a method to create a stream in object mode', function test( t ) {
	t.equal( typeof splitStream.objectMode, 'function', 'has method' );
	t.end();
});

tape( 'attached to main export is a method to create a stream factory', function test( t ) {
	t.equal( typeof splitStream.factory, 'function', 'has method' );
	t.end();
});
