'use strict';

// MODULES //

var tape = require( 'tape' );
var transformStream = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof transformStream, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to main export is a method to create a stream in object mode', function test( t ) {
	t.equal( typeof transformStream.objectMode, 'function', 'has method' );
	t.end();
});

tape( 'attached to main export is a method to create a stream factory', function test( t ) {
	t.equal( typeof transformStream.factory, 'function', 'has method' );
	t.end();
});

tape( 'attached to main export is a method to create a stream constructor', function test( t ) {
	t.equal( typeof transformStream.ctor, 'function', 'has method' );
	t.end();
});
