'use strict';

var discreteUniform = require( '@stdlib/random/base/discrete-uniform' ).factory;
var filledarray = require( '@stdlib/array/filled' );
var gfillBy = require( '@stdlib/blas/ext/base/gfill-by' );
var dtypes = require( '@stdlib/array/dtypes' );
var binary = require( './../lib' );

function add( x, y ) {
	return x + y;
}

var arrays = [ null, null, null ];
var shape = [ 10 ];
var strides = [ 1, 1, -1 ];
var offsets = [ 0, 0, shape[0]-1 ];

var dt = dtypes();

var x;
var y;
var z;
var i;
for ( i = 0; i < dt.length; i++ ) {
	x = filledarray( 0.0, shape[ 0 ], dt[ i ] );
	gfillBy( x.length, x, 1, discreteUniform( -100, 100 ) );
	console.log( x );

	y = filledarray( 0.0, shape[ 0 ], dt[ i ] );
	gfillBy( y.length, y, 1, discreteUniform( -100, 100 ) );
	console.log( y );

	z = filledarray( 0.0, x.length, dt[ i ] );
	console.log( z );

	arrays[ 0 ] = x;
	arrays[ 1 ] = y;
	arrays[ 2 ] = z;
	binary.ndarray( arrays, shape, strides, offsets, add );
	console.log( z );
	console.log( '' );
}
