/**
* Header file for strided array data types.
*/
#ifndef STDLIB_STRIDED_DTYPES_H
#define STDLIB_STRIDED_DTYPES_H

enum STDLIB_STRIDED_DTYPES {
	STDLIB_BOOL = 0,

	STDLIB_INT8 = 1,
	STDLIB_UINT8 = 2,
	STDLIB_INT16 = 3,
	STDLIB_UINT16 = 4,
	STDLIB_INT32 = 5,
	STDLIB_UINT32 = 6,
	STDLIB_INT64 = 7,
	STDLIB_UINT64 = 8,
	STDLIB_INT128 = 9,
	STDLIB_UINT128 = 10,

	STDLIB_FLOAT16 = 20,
	STDLIB_FLOAT32 = 21,
	STDLIB_FLOAT64 = 22,
	STDLIB_FLOAT128 = 23
};

#endif // !STDLIB_STRIDED_DTYPES_H
