/**
* Functionality for creating, and interfacing with, ndarrays.
*/
#include <stdlib.h>
#include <stdint.h>
#include "stdlib/ndarray.h"

/**
* Returns a pointer to a dynamically allocated ndarray.
*
*
* @return pointer to a dynamically allocated ndarray or, if unable to allocate memory, a null pointer
*
* @example
* TODO
*/
struct ndarray * ndarray_constructor( uint8_t *data, uint64_t ndims, int64_t *shape, int64_t *strides, uint64_t offset, enum STDLIB_NDARRAY_ORDER order, enum STDLIB_STRIDED_DTYPES dtype, int flags ) {
	uint64_t len;
	uint64_t i;

	struct ndarray *arr = malloc( sizeof( struct ndarray ) );
	if ( arr == NULL ) {
		return NULL;
	}
	arr->data = data;
	arr->ndims = ndims;
	arr->shape = shape;
	arr->strides = strides;
	arr->offset = offset;
	arr->order = order;
	arr->dtype = dtype;
	arr->flags = flags;

	len = 0;
	for ( i = 0; i < ndims; i++ ) {
		len += shape[ i ];
	}
	arr->length = len;

	return arr;
}
