'use strict';

// MAIN //

/**
* Copies a list of properties from a source object to a target object as enumerable properties.
*
* @private
* @param {Object} target - target object
* @param {Object} source - source object
* @param {ArrayLikeObject} keys - list of properties
* @returns {Object} modified target object
*
* @example
* var source = {
*     'beep': 'boop'
* };
* var target = {};
*
* var out = pick( target, source, [ 'beep' ] );
* // returns <Object>
*
* var bool = ( out === target );
* // returns true
*
* var v = target.beep;
* // returns 'boop'
*/
function pick( target, source, keys ) {
	var k;
	var i;
	for ( i = 0; i < keys.length; i++ ) {
		k = keys[ i ];
		target[ k ] = source[ k ];
	}
	return target;
}


// EXPORTS //

module.exports = pick;
