'use strict';

var randu = require( '@stdlib/random/base/randu' );
var round = require( '@stdlib/math/base/special/round' );
var typedarray = require( '@stdlib/array/typed' );
var dtypes = require( '@stdlib/array/typed-dtypes' );
var abs = require( './../lib' );

function random( N, dtype ) {
	var rand;
	var sign;
	var x;
	var i;

	x = typedarray( N, dtype );
	for ( i = 0; i < N; i++ ) {
		rand = round( randu()*100.0 );
		sign = randu();
		if ( sign < 0.5 ) {
			sign = -1.0;
		} else {
			sign = 1.0;
		}
		x[ i ] = sign * rand;
	}
	return x;
}

var dt;
var x;
var y;
var i;

dt = dtypes();
for ( i = 0; i < dt.length; i++ ) {
	x = random( 10, dt[ i ] );
	y = typedarray( x.length, dt[ i ] );

	console.log( x );
	console.log( y );

	abs.ndarray( x.length, x, 1, 0, y, -1, y.length-1 );
	console.log( y );
	console.log( '' );
}
