'use strict';

// MODULES //

var tape = require( 'tape' );
var UNICODE_MAX_BMP_CODE_POINT = require( './../lib' ); // eslint-disable-line id-length


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof UNICODE_MAX_BMP_CODE_POINT, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is 65535', function test( t ) {
	t.equal( UNICODE_MAX_BMP_CODE_POINT, 65535, 'equals 65535' );
	t.end();
});
