'use strict';

// MODULES //

var tape = require( 'tape' );
var UNICODE_MAX = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof UNICODE_MAX, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is 1114111', function test( t ) {
	t.equal( UNICODE_MAX, 1114111, 'equals 1114111' );
	t.end();
});
