'use strict';

var endsWith = require( './../lib' );

var str = 'Fair is foul, and foul is fair, hover through fog and filthy air';

console.log( endsWith( str, 'air' ) );
// returns true

console.log( endsWith( str, 'fair' ) );
// returns false

console.log( endsWith( str, 'fair', 30 ) );
// returns true
