'use strict';

var escapeRegexp = require( './../lib' );

var str = 'Et tu, Brute?';
console.log( escapeRegexp( str ) );
// => 'Et tu, Brute\?

str = '2 + 3 * 4 = 12';
console.log( escapeRegexp( str ) );
// => '2 \+ 3 \* 4 = 12'

str = 'The US federal budget was $3.8 trillion (in 2015)';
console.log( escapeRegexp( str ) );
// => 'The US federal budget was \$3\.8 trillion \(in 2015\)'

str = '';
console.log( escapeRegexp( str ) );
// => ''
