'use strict';

// MODULES //

var bench = require( '@stdlib/bench' );
var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var fromCodePoint = require( '@stdlib/string/from-code-point' );
var pkg = require( './../package.json' ).name;
var ltrim = require( './../lib' );


// VARIABLES //

var opts = {
	'skip': ( typeof String.prototype.trimLeft !== 'function' )
};


// MAIN //

bench( pkg, function benchmark( b ) {
	var whitespace;
	var str;
	var out;
	var i;

	whitespace = '\\u0020\\f\\n\\r\\t\\v\\u00a0\\u1680\\u2000-\\u200a\\u2028\\u2029\\u202f\\u205f\\u3000\\ufeff';

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		str = whitespace + fromCodePoint( i%126 ) + 'eep boop';
		out = ltrim( str );
		if ( !isString( out ) ) {
			b.fail( 'should return a string' );
		}
	}
	b.toc();
	if ( !isString( out ) ) {
		b.fail( 'should return a string' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});

bench( pkg+'::built-in', opts, function benchmark( b ) {
	var whitespace;
	var str;
	var out;
	var i;

	whitespace = '\\u0020\\f\\n\\r\\t\\v\\u00a0\\u1680\\u2000-\\u200a\\u2028\\u2029\\u202f\\u205f\\u3000\\ufeff';

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		str = whitespace + fromCodePoint( i%126 ) + 'eep boop';
		out = str.trimLeft();
		if ( !isString( out ) ) {
			b.fail( 'should return a string' );
		}
	}
	b.toc();
	if ( !isString( out ) ) {
		b.fail( 'should return a string' );
	}
	b.pass( 'benchmark finished' );
	b.end();
});
