'use strict';

var lowercase = require( './../lib' );

var str;

str = lowercase( 'Beep' );
console.log( str );
// => 'beep'

str = lowercase( 'BeEp' );
console.log( str );
// => 'beep'

str = lowercase( 'Beep BOOP' );
console.log( str );
// => 'beep boop'

str = lowercase( '$**_Beep_BoOp_**$' );
console.log( str );
// => '$**_beep_boop_**$'

str = lowercase( '' );
console.log( str );
// => ''
