'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;


// MAIN //

/**
* Returns a string with the first character converted to lowercase.
*
* @param {string} str - input string
* @throws {TypeError} must provide a string primitive
* @returns {string} string with lower-cased first character
*
* @example
* var out = lowerfirst( 'Last Man Standing' );
* // returns 'last Man Standing'
*
* @example
* var out = lowerfirst( 'Presidential election' );
* // returns 'presidential election'
*
* @example
* var out = lowerfirst( 'JavaScript' );
* // returns 'javaScript'
*
* @example
* var out = lowerfirst( 'Hidden Treasures' );
* // returns 'hidden Treasures'
*/
function lowerfirst( str ) {
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid argument. First argument must be a string primitive. Value: `' + str + '`.' );
	}
	if ( str === '' ) {
		return '';
	}
	return str.charAt( 0 ).toLowerCase() + str.slice( 1 );
}


// EXPORTS //

module.exports = lowerfirst;
