'use strict';

// MODULES //

var tape = require( 'tape' );
var lowerfirst = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof lowerfirst, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function throws an error if not provided a string', function test( t ) {
	var values;
	var i;

	values = [
		5,
		null,
		true,
		void 0,
		NaN,
		[],
		{},
		function noop() {}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.throws( badValue( values[i] ), TypeError, 'throws an error when provided '+values[i] );
	}
	t.end();

	function badValue( value ) {
		return function badValue() {
			lowerfirst( value );
		};
	}
});

tape( 'the function returns an empty string if provided an empty string', function test( t ) {
	t.equal( lowerfirst( '' ), '', 'returns empty string' );
	t.end();
});

tape( 'the function converts the first character of a string to lowercase', function test( t ) {
	var out;

	out = lowerfirst( 'Hello World!' );
	t.equal( out, 'hello World!', 'turns first character to lowercase' );

	out = lowerfirst( '!!!' );
	t.equal( out, '!!!', 'does not change non-alphabetical characters' );

	out = lowerfirst( 'hello world' );
	t.equal( out, 'hello world', 'leaves lowercase letters in lowercase' );

	t.end();
});
