'use strict';

var percentEncode = require( './../lib' );

var values;
var out;
var i;

values = [
	'Ladies + Gentlemen',
	'An encoded string!',
	'Dogs, Cats & Mice',
	'☃',
	'æ',
	'𐐷'
];
for ( i = 0; i < values.length; i++ ) {
	out = percentEncode( values[ i ] );
	console.log( '%s: %s', values[ i ], out );
}
