'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;


// MAIN //

/**
* Removes the first character of a string.
*
* @param {string} str - input string
* @throws {TypeError} must provide a string primitive
* @returns {string} updated string
*
* @example
* var out = removeFirst( 'last man standing' );
* // returns 'ast man standing'
*
* @example
* var out = removeFirst( 'presidential election' );
* // returns 'presidential Election'
*
* @example
* var out = removeFirst( 'javaScript' );
* // returns 'javaScript'
*
* @example
* var out = removeFirst( 'Hidden Treasures' );
* // returns 'hidden Treasures';
*/
function removeFirst( str ) {
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid input argument. First argument must be a string primitive. Value: `' + str + '`.' );
	}
	if ( str === '' ) {
		return '';
	}
	return str.substring( 1 );
} // end FUNCTION removeFirst()


// EXPORTS //

module.exports = removeFirst;
