'use strict';

var removeBOM = require( './../lib' );

var str = removeBOM( '\ufeffbeep' );
console.log( str );
// => 'beep'

str = removeBOM( 'boop\ufeff' );
console.log( str );
// => 'boop\ufeff'

str = removeBOM( 'be\ufeffbop' );
console.log( str );
// => 'be\ufeffbop'

str = removeBOM( 'foobar' );
console.log( str );
// => foobar
