'use strict';

var removeUTF8BOM = require( './../lib' );

var str = removeUTF8BOM( '\ufeffbeep' );
console.log( str );
// => 'beep'

str = removeUTF8BOM( 'boop\ufeff' );
console.log( str );
// => 'boop\ufeff'

str = removeUTF8BOM( 'be\ufeffbop' );
console.log( str );
// => 'be\ufeffbop'

str = removeUTF8BOM( 'foobar' );
console.log( str );
// => 'foobar'
