'use strict';

/**
* Right pads a string such that the padded string has a length of at least `len`.
*
* @module @stdlib/utils/right-pad-string
*
* @example
* var rpad = require( '@stdlib/utils/right-pad-string' );
*
* var str = rpad( 'a', 5 );
* // returns 'a    '
*
* str = rpad( 'beep', 10, 'p' );
* // returns 'beeppppppp'
*
* str = rpad( 'beep', 12, 'boop' );
* // returns 'beepboopboop'
*/

// MODULES //

var rpad = require( './right_pad_string.js' );


// EXPORTS //

module.exports = rpad;
