'use strict';

var round = require( '@stdlib/math/base/special/round' );
var randu = require( '@stdlib/math/base/random/randu' );
var rpad = require( './../lib' );

var str = 'beep';
var n;
var i;

for ( i = 0; i < 100; i++ ) {
	n = round( randu()*10.0 ) + str.length;
	console.log( rpad( str, n, 'p' ) );
}
