'use strict';

// MODULES //

var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var replace = require( '@stdlib/string/replace' );


// VARIABLES //

var RE = new RegExp( '[\t\r\n ]+$' );


// RIGHT TRIM //

/**
* Trim whitespace from end of a string.
*
* @param {string} str - input string
* @throws {TypeError} str must be a string primitive
* @returns {string} output string
*
* @example
* var out = rtrim( '   Whitespace   ' );
* // returns '   Whitespace'
* @example
* var out = rtrim( '\t\t\tTabs\t\t\t' );
* // returns '\t\t\tTabs'
* @example
* var out = rtrim( '\n\n\nNew Lines\n\n\n' );
* // returns '\n\n\nNew Lines'
*/
function rtrim( str ) {
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid input argument. First argument must be a string primitive. Value: `' + str + '`.' );
	}
	return replace( str, RE, '' );
} // end FUNCTION rtrim()


// EXPORTS //

module.exports = rtrim;
