'use strict';

var startcase = require( './../lib' );

var str;

str = startcase( 'beep boop foo bar' );
console.log( str );
// => 'Beep Boop Foo Bar'

str = startcase( 'Beep' );
console.log( str );
// => 'Beep'

str = startcase( 'BeEp' );
console.log( str );
// => 'BeEp'

str = startcase( '$**_beep_BoOp_**$' );
console.log( str );
// => '$**_beep_BoOp_**$'

str = startcase( '' );
console.log( str );
// => ''
