'use strict';

// MODULES //

var isString = require( '@stdlib/assert/is-string' ).isPrimitive;
var RE_WHITESPACE = require( '@stdlib/regexp/whitespace' );


// MAIN //

/**
* Capitalizes the first letter of each word in an input string.
*
* @param {string} str - string to convert
* @throws {TypeError} must provide a primitive string
* @returns {string} start case string
*
* @example
* var str = startcase( 'beep boop foo bar' );
* // returns 'Beep Boop Foo Bar'
*/
function startcase( str ) {
	var cap;
	var out;
	var ch;
	var i;
	if ( !isString( str ) ) {
		throw new TypeError( 'invalid input argument. Must provide a primitive string. Value: `'+str+'`.' );
	}
	cap = true;
	out = '';
	for ( i = 0; i < str.length; i++ ) {
		ch = str.charAt( i );
		if ( RE_WHITESPACE.test( ch ) ) {
			out += ch;
			cap = true;
		} else if ( cap ) {
			out += ch.toUpperCase();
			cap = false;
		} else {
			out += ch;
		}
	}
	return out;
} // end FUNCTION startcase()


// EXPORTS //

module.exports = startcase;
